import React, { useState, useEffect } from 'react';
import { fetchBooks } from '../services/api';
import { Card, Button, Spinner, Alert, Form, InputGroup } from 'react-bootstrap';
import BookModal from './BookModal';
import { useNavigate } from 'react-router-dom';

const BookList = () => {
    const [books, setBooks] = useState([]); // Initialize books as an empty array
    const [selectedBook, setSelectedBook] = useState(null); // Track the selected book
    const [showModal, setShowModal] = useState(false);
    const [loading, setLoading] = useState(true); // Track loading state
    const [error, setError] = useState(null); // Track error state

    // Search parameters
    const [searchTerm, setSearchTerm] = useState('');
    const [searchYear, setSearchYear] = useState('');
    const [searchType, setSearchType] = useState('');

    const navigate = useNavigate(); // useNavigate hook for routing

    useEffect(() => {
        const getBooks = async () => {
            setLoading(true); // Start loading
            setError(null); // Clear previous errors
            try {
                const response = await fetchBooks({
                    name: searchTerm,
                    year: searchYear,
                    type: searchType,
                });
                setBooks(response); // Set books to response directly
            } catch (err) {
                console.error("Error fetching books:", err); // Log the actual error
                setError("Error fetching books. Please try again later."); // Handle fetch errors
            } finally {
                setLoading(false); // Set loading to false regardless of success or failure
            }
        };

        getBooks();
    }, [searchTerm, searchYear, searchType]); // Run when search parameters change

    const handleBookClick = (book) => {
        setSelectedBook(book);
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
        setSelectedBook(null);
    };

    // Use the navigate function to go to the reservations page
    const handleGoToReservations = () => {
        navigate('/reservations');
    };

    if (loading) {
        return <Spinner animation="border" className="mt-4" />; // Show loading spinner while fetching
    }

    if (error) {
        return <Alert variant="danger" className="mt-4">{error}</Alert>; // Display error message if fetch failed
    }

    return (
        <div className="container mt-4">
            <h2>Books List</h2>
            <InputGroup className="mb-3">
                <Form.Control
                    type="text"
                    placeholder="Search by name"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)} // Set search term
                    onFocus={(e) => e.target.select()} // Select input text on focus
                />
                <Form.Control
                    type="number"
                    placeholder="Year"
                    value={searchYear}
                    onChange={(e) => setSearchYear(e.target.value)} // Set search year
                    onFocus={(e) => e.target.select()} // Select input text on focus
                />
                <Form.Select
                    value={searchType}
                    onChange={(e) => setSearchType(e.target.value)} // Set search type
                >
                    <option value="">All Types</option>
                    <option value="Book">Book</option>
                    <option value="Audiobook">Audiobook</option>
                </Form.Select>
            </InputGroup>

            {/* Books list */}
            <div className="row">
                {books.length > 0 ? (
                    books.map((book) => (
                        <div key={book.id} className="col-md-4 mb-3">
                            <Card>
                                <Card.Img variant="top" src={book.pictureUrl} alt={book.name} />
                                <Card.Body>
                                    <Card.Title>{book.name}</Card.Title>
                                    <Card.Text>Year: {book.year}</Card.Text>
                                    <Card.Text>Type: {book.type}</Card.Text>
                                    <Button variant="primary" onClick={() => handleBookClick(book)}>
                                        Reserve
                                    </Button>
                                </Card.Body>
                            </Card>
                        </div>
                    ))
                ) : (
                    <p>No books available</p> // Handle case where there are no books
                )}
            </div>

            {/* Book reservation modal */}
            {selectedBook && (
                <BookModal
                    show={showModal}
                    handleClose={handleCloseModal}
                    book={selectedBook}
                />
            )}

            {/* Button to go to reservations page */}
            <Button variant="secondary" onClick={handleGoToReservations}>
                Go to Reservations
            </Button>

            <Button variant="danger" className="ms-2" onClick={() => {
                localStorage.removeItem('user');  // Remove user from localStorage
                navigate('/login');               // Redirect to login page
            }}>
                Log Out
            </Button>
        </div>
    );
};

export default BookList;
