// src/components/Login.js
import React, { useState } from 'react';
import { Form, Button, Alert, Spinner } from 'react-bootstrap';
import { loginUser } from '../services/api';  // Assuming loginUser is the API function for login
import { useHistory } from 'react-router-dom';  // To redirect after successful login

const Login = () => {
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const history = useHistory();  // Use history for redirection

    const handleLogin = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            const response = await loginUser({ name, password });  // Send login request
            localStorage.setItem('token', response.token);  // Store token or user info
            alert('Login successful!');
            history.push('/books');  // Redirect to books page after login
        } catch (err) {
            setError('Invalid login credentials.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="container mt-4">
            <h2>Login</h2>
            <Form onSubmit={handleLogin}>
                {error && <Alert variant="danger">{error}</Alert>}

                <Form.Group controlId="name">
                    <Form.Label>Name</Form.Label>
                    <Form.Control
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        placeholder="Enter your name"
                        required
                    />
                </Form.Group>

                <Form.Group controlId="password">
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Enter your password"
                        required
                    />
                </Form.Group>

                <Button variant="primary" type="submit" disabled={loading}>
                    {loading ? <Spinner as="span" animation="border" size="sm" /> : 'Login'}
                </Button>
            </Form>
        </div>
    );
};

export default Login;
