import React, { useEffect, useState } from 'react';
import { fetchBooks } from './api'; // Adjust the import based on your structure

const BookList = () => {
    const [books, setBooks] = useState([]); // Initialize to an empty array
    const [error, setError] = useState(null);

    useEffect(() => {
        const getBooks = async () => {
            try {
                const fetchedBooks = await fetchBooks();
                setBooks(fetchedBooks);
            } catch (err) {
                setError(err.message); // Handle error if fetching fails
            }
        };

        getBooks();
    }, []);

    // If you want to handle error rendering
    if (error) {
        return <div>Error fetching books: {error}</div>;
    }

    return (
        <div>
            {books.length > 0 ? (
                books.map(book => (
                    <Book key={book.id} book={book} />
                ))
            ) : (
                <p>No books available</p>
            )}
        </div>
    );
};

export default BookList;
