// src/services/api.js
import axios from 'axios';

const api = axios.create({
    baseURL: 'https://localhost:7212/api',
});

api.interceptors.request.use((config) => {
    const user = JSON.parse(localStorage.getItem('user'));
    if (user && user.token) {  // Assuming 'token' is part of the user object
        config.headers.Authorization = `Bearer ${user.token}`;
    }
    return config;
}, (error) => {
    return Promise.reject(error);
});

export const fetchBooks = async (searchParams) => {
    try {
        const response = await api.get('/books', { params: searchParams });
        return response.data;
    } catch (error) {
        console.error('Error fetching books:', error);
        throw error;
    }
};

// Create a reservation
export const createReservation = async (data) => {
    try {
        const response = await api.post('/reservations', data);
        return response.data;
    } catch (error) {
        console.error('Error creating reservation:', error);
        throw error;
    }
};

// Fetch reservations for a specific user
export const fetchReservations = async (userId) => {
    try {
        const response = await api.get('/reservations', { params: { userId } });
        return response.data || []; // Return the data or an empty array
    } catch (error) {
        console.error('Error fetching reservations:', error);
        // Check if error is 404
        if (error.response && error.response.status === 404) {
            // If not found, return an empty array
            return []; // Or handle accordingly
        }
        throw error.response?.data || 'Error fetching reservations'; // Handle other errors
    }
};

// Login a user
export const loginUser = async (credentials) => {
    try {
        const response = await api.post('/auth/login', credentials);
        return response.data;
    } catch (error) {
        console.error('Login failed:', error);
        throw error.response?.data || 'Login failed';
    }
};

// Register a user
export const registerUser = async (credentials) => {
    try {
        const response = await api.post('/auth/register', credentials);
        return response.data;
    } catch (error) {
        console.error('Registration failed:', error);
        throw error.response?.data || 'Registration failed';
    }
};
