import React, { useState } from 'react';
import { Form, Button, Alert, Spinner } from 'react-bootstrap';
import { registerUser } from '../services/api';  // Assuming registerUser is the API function for registration
import { useNavigate } from 'react-router-dom';

const Registration = () => {
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    const navigate = useNavigate();

    const handleRegistration = async (e) => {
        e.preventDefault();
        setLoading(true);
        setError('');
        setSuccess('');

        try {
            const response = await registerUser({ name, password });
            setSuccess('Registration successful! You can now login.');
            navigate('/login');  // Redirect to login after successful registration
        } catch (err) {
            setError('Registration failed. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    // Function to handle navigation to the main menu
    const handleBackToMainMenu = () => {
        navigate('/'); // Assuming '/' is your main menu route
    };

    return (
        <div className="container mt-4">
            <h2>Register</h2>
            <Form onSubmit={handleRegistration}>
                {error && <Alert variant="danger">{error}</Alert>}
                {success && <Alert variant="success">{success}</Alert>}

                <Form.Group controlId="name">
                    <Form.Label>Name</Form.Label>
                    <Form.Control
                        type="text"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        placeholder="Enter your name"
                        required
                    />
                </Form.Group>

                <Form.Group controlId="password" className="mt-3">
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                        type="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Enter your password"
                        required
                    />
                </Form.Group>

                <Button className="mt-3" variant="primary" type="submit" disabled={loading}>
                    {loading ? <Spinner as="span" animation="border" size="sm" /> : 'Register'}
                </Button>

                {/* Button to go back to the main menu */}
                <Button className="mt-3 ms-2" variant="secondary" onClick={handleBackToMainMenu}>
                    Back to Main Menu
                </Button>
            </Form>
        </div>
    );
};

export default Registration;
