// src/components/BookList.js
import React, { useState, useEffect } from 'react';
import { fetchBooks } from '../services/api';
import { Card, Button, Spinner, Alert } from 'react-bootstrap';
import BookModal from './BookModal';

const BookList = () => {
    const [books, setBooks] = useState([]); // Initialize books as an empty array
    const [selectedBook, setSelectedBook] = useState(null); // Track the selected book
    const [showModal, setShowModal] = useState(false);
    const [loading, setLoading] = useState(true); // Track loading state
    const [error, setError] = useState(null); // Track error state

    useEffect(() => {
        const getBooks = async () => {
            try {
                const response = await fetchBooks(); // Fetch the books
                setBooks(response); // Set books to response directly
            } catch (err) {
                setError("Error fetching books. Please try again later."); // Handle fetch errors
            } finally {
                setLoading(false); // Set loading to false regardless of success or failure
            }
        };

        getBooks();
    }, []);

    const handleBookClick = (book) => {
        setSelectedBook(book);
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
        setSelectedBook(null);
    };

    const handleGoToReservations = () => {
        window.location.href = '/reservations'; // Function to handle button click
    };

    if (loading) {
        return <Spinner animation="border" />; // Show loading spinner while fetching
    }

    if (error) {
        return <Alert variant="danger">{error}</Alert>; // Display error message if fetch failed
    }

    return (
        <div className="container mt-4">
            <h2>Books List</h2>
            <div className="row">
                {books.length > 0 ? (
                    books.map((book) => (
                        <div key={book.id} className="col-md-4">
                            <Card>
                                <Card.Img variant="top" src={book.pictureUrl} alt={book.name} />
                                <Card.Body>
                                    <Card.Title>{book.name}</Card.Title>
                                    <Card.Text>Year: {book.year}</Card.Text>
                                    <Card.Text>Type: {book.type}</Card.Text>
                                    <Button variant="primary" onClick={() => handleBookClick(book)}>
                                        Reserve
                                    </Button>
                                </Card.Body>
                            </Card>
                        </div>
                    ))
                ) : (
                    <p>No books available</p> // Handle case where there are no books
                )}
            </div>

            {selectedBook && (
                <BookModal
                    show={showModal}
                    handleClose={handleCloseModal}
                    book={selectedBook}
                />
            )}

            <Button variant="secondary" onClick={handleGoToReservations}>
                TO Reservations
            </Button>
        </div>
    );
};

export default BookList;
