// src/components/ReservationList.js
import React, { useState, useEffect } from 'react';
import { fetchReservations } from '../services/api';
import { Table, Button, Spinner, Alert } from 'react-bootstrap';
import {useHistory} from "react-router-dom";

const ReservationList = () => {
    const history = useHistory();

    useEffect(() => {
        const token = localStorage.getItem('token');
        if (!token) {
            history.push('/login');  // Redirect to login if no token
        }
    }, [history]);
    const [reservations, setReservations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    
    const userId = "user123";

    useEffect(() => {
        const getReservations = async () => {
            try {
                const response = await fetchReservations(userId);
                console.log(response); // Log to see what's returned
                setReservations(response);
            } catch (err) {
                setError("Error fetching reservations. Please try again.");
            } finally {
                setLoading(false);
            }
        };

        getReservations();
    }, []);

    if (loading) {
        return <Spinner animation="border" />; // Show loading spinner
    }

    if (error) {
        return <Alert variant="danger">{error}</Alert>; // Display error message
    }

    if (reservations.length === 0) {
        return <p>No reservations found.</p>; // Handle no reservations
    }

    const handleGoToBooks = () => {
        window.location.href = '/books'; // Redirect to books page
    };

    return (
        <div className="container mt-4">
            <h2>My Reservations</h2>
            <Table striped bordered hover>
                <thead>
                <tr>
                    <th>Book Name</th>
                    <th>Type</th>
                    <th>Days</th>
                    <th>Quick Pickup</th>
                    <th>Total Price</th>
                </tr>
                </thead>
                <tbody>
                {reservations.map((reservation) => (
                    <tr key={reservation.id}>
                        <td>{reservation.bookName}</td>
                        <td>{reservation.type}</td>
                        <td>{reservation.days}</td>
                        <td>{reservation.quickPickup ? 'Yes' : 'No'}</td>
                        <td>â‚¬{(reservation.totalPrice || 0).toFixed(2)}</td>
                        {/* Ensure you use the correct property name */}
                    </tr>
                ))}
                </tbody>

            </Table>
            <Button variant="secondary" onClick={handleGoToBooks}>
                Go to Books
            </Button>
        </div>
    );
};

export default ReservationList;
