import React, { useState, useEffect } from 'react';
import { fetchReservations } from '../services/api';
import { Table } from 'react-bootstrap';

const ReservationList = () => {
    const [reservations, setReservations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const userId = "user1231"; // Mock user ID (should match the backend user ID)

    useEffect(() => {
        const getReservations = async () => {
            try {
                const response = await fetchReservations(userId);
                console.log('Reservations:', response); // Debug: Log reservations
                setReservations(response);
            } catch (err) {
                console.error('Error fetching reservations:', err); // Log the error
                setError("Error fetching reservations. Please try again.");
            } finally {
                setLoading(false);
            }
        };

        getReservations();
    }, []);

    if (loading) {
        return <p>Loading...</p>;
    }

    if (error) {
        return <p>{error}</p>;
    }

    if (reservations.length === 0) {
        return <p>No reservations found.</p>;
    }

    return (
        <div className="container mt-4">
            <h2>My Reservations</h2>
            <Table striped bordered hover>
                <thead>
                <tr>
                    <th>Book Name</th>
                    <th>Type</th>
                    <th>Days</th>
                    <th>Quick Pickup</th>
                    <th>Total Price</th>
                </tr>
                </thead>
                <tbody>
                {reservations.map((reservation) => (
                    <tr key={reservation.id}>
                        <td>{reservation.bookName}</td>
                        <td>{reservation.type}</td>
                        <td>{reservation.days}</td>
                        <td>{reservation.quickPickup ? 'Yes' : 'No'}</td>
                        <td>â‚¬{reservation.totalPrice.toFixed(2)}</td> {/* Ensure totalPrice is defined */}
                    </tr>
                ))}
                </tbody>
            </Table>
        </div>
    );
};

export default ReservationList;
