// src/components/BookList.js
import React, { useState, useEffect } from 'react';
import { fetchBooks } from '../services/api';
import { Card, Button } from 'react-bootstrap';
import BookModal from './BookModal';

const BookList = () => {
    const [books, setBooks] = useState([]);
    const [selectedBook, setSelectedBook] = useState(null); // Track the selected book
    const [showModal, setShowModal] = useState(false);

    useEffect(() => {
        fetchBooks().then((response) => setBooks(response.data));
    }, []);

    const handleBookClick = (book) => {
        setSelectedBook(book);
        setShowModal(true);
    };

    const handleCloseModal = () => {
        setShowModal(false);
        setSelectedBook(null);
    };

    return (
        <div className="container mt-4">
            <h2>Books List</h2>
            <div className="row">
                {books.map((book) => (
                    <div key={book.id} className="col-md-4">
                        <Card>
                            <Card.Img variant="top" src={book.pictureUrl} alt={book.name} />
                            <Card.Body>
                                <Card.Title>{book.name}</Card.Title>
                                <Card.Text>Year: {book.year}</Card.Text>
                                <Card.Text>Type: {book.type}</Card.Text>
                                <Button variant="primary" onClick={() => handleBookClick(book)}>
                                    Reserve
                                </Button>
                            </Card.Body>
                        </Card>
                    </div>
                ))}
            </div>

            {selectedBook && (
                <BookModal
                    show={showModal}
                    handleClose={handleCloseModal}
                    book={selectedBook}
                />
            )}
        </div>
    );
};

export default BookList;
