// src/App.js
import React from 'react';
import { Navigate, Route, Routes } from 'react-router-dom';
import BookList from './components/BookList';
import ReservationList from './components/ReservationList';
import Login from "./components/Login";
import Registration from "./components/Registration";
import 'bootstrap/dist/css/bootstrap.min.css';

function App() {
    const user = JSON.parse(localStorage.getItem('user'));  // Check if the user is logged in

    return (
        <div>
            <Routes>
                <Route path="/login" element={<Login />} />
                <Route path="/register" element={<Registration />} />
                <Route path="/books" element={user ? <BookList /> : <Navigate to="/login" />} />
                <Route path="/reservations" element={user ? <ReservationList /> : <Navigate to="/login" />} />
                <Route path="*" element={<Navigate to={user ? "/books" : "/login"} />} />
            </Routes>
        </div>
    );
}

export default App;
