import React, { useState, useEffect } from 'react';
import { fetchReservations } from '../services/api';
import { Table, Button } from 'react-bootstrap';

const ReservationList = () => {
    const [reservations, setReservations] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    // Replace with actual user ID if available
    const userId = "user1231";

    useEffect(() => {
        const getReservations = async () => {
            try {
                const response = await fetchReservations(userId);
                console.log(response); // Debug: Log reservations data
                setReservations(response);
            } catch (err) {
                setError("Error fetching reservations. Please try again.");
            } finally {
                setLoading(false);
            }
        };

        getReservations();
    }, []);

    if (loading) {
        return <p>Loading...</p>;
    }

    if (error) {
        return <p>{error}</p>;
    }

    if (reservations.length === 0) {
        return <p>No reservations found.</p>;
    }

    const handleGoToBooks = () => {
        window.location.href = '/books'; // Function to navigate to the books page
    };

    return (
        <div className="container mt-4">
            <h2>My Reservations</h2>
            <Table striped bordered hover>
                <thead>
                <tr>
                    <th>Book Name</th>
                    <th>Type</th>
                    <th>Days</th>
                    <th>Quick Pickup</th>
                    <th>Total Price</th>
                </tr>
                </thead>
                <tbody>
                {reservations.map((reservation) => (
                    <tr key={reservation.id}>
                        <td>{reservation.bookName}</td>
                        <td>{reservation.type}</td>
                        <td>{reservation.days}</td>
                        <td>{reservation.quickPickup ? 'Yes' : 'No'}</td>
                        <td>â‚¬{(reservation.Totalprice || 0).toFixed(2)}</td> {/* Safeguard totalPrice */}
                    </tr>
                ))}
                </tbody>
            </Table>
            <Button variant="secondary" onClick={handleGoToBooks}>
                Go to Books
            </Button>
        </div>
    );
};

export default ReservationList;
